 -- 제품 지우기 전에 제품 사진들 지우기 -jongyoon
delimiter $$
create trigger delete_pro_img
before delete on product
for each row
begin
delete from pro_img
where pro_num = old.pro_num;
end $$
delimiter ;

delimiter $$
create trigger delete_replycount
before delete on product
for each row
BEGIN
UPDATE
   SET re_replyCount = old.re_replyCount-1
from product
end $$
delimiter ;

delimiter $$
create trigger delete_re_reply
before delete on review
for each row
begin
delete from re_reply
where re_num = old.re_num;
end $$
delimiter ;